package org.mnode.ical4j.serializer.jotn.component

import com.fasterxml.jackson.databind.ObjectMapper
import com.fasterxml.jackson.databind.module.SimpleModule
import net.fortuna.ical4j.model.component.VEvent
import org.mnode.ical4j.serializer.AbstractSerializerTest

class VEventSerializerTest extends AbstractSerializerTest {

    def 'test event serialization'() {
        given: 'an object mapper'
        SimpleModule module = []
        module.addSerializer(VEvent, new VEventSerializer())
        ObjectMapper mapper = []
        mapper.registerModule(module)

        when: 'a event is serialized'
        String serialized = mapper.writeValueAsString(event)

        then: 'serialized string matches expected value'
        serialized == expectedSerialized

        where:
        event    | expectedSerialized
        event1   | '{"uid":"1","attach":{"encoding":"BASE64","value":"BINARY","binary":""},"summary":"Test Event 1","dtstart":{"value":"DATE","date":"2009-08-10"},"organizer":"johnd@example.com"}'
        event2   | '{"uid":"2","summary":"Test Event 2","description":{"x-format":"text/plain","value":"Test Description 2"},"dtstart":{"value":"DATE","date":"2010-08-10"},"organizer":{"cn":"John Doe","cal-address":"johnd@example.com"}}'
        event3   | '{"summary":"New Years Day","dtstart":"20240101","categories":["holidays","international,global"]}'
    }
}
